PACKAGE company_api IS
  PROCEDURE hire_employee(p_name IN VARCHAR2);
END company_api;

PACKAGE BODY company_api IS
  PROCEDURE hire_employee(p_name IN VARCHAR2) IS
  BEGIN
    INSERT INTO employees (name) VALUES (p_name);
  END hire_employee;
END company_api;

PACKAGE BODY my_pkg IS
  FUNCTION is_valid(p_id IN NUMBER) RETURN BOOLEAN IS
  BEGIN
    RETURN p_id IS NOT NULL;
  END;

  PROCEDURE public_proc IS
  BEGIN
    IF is_valid(123) THEN NULL; END IF;
  END;
END my_pkg;

BEGIN
  -- First call loads my_package into memory
  my_package.proc1();
  -- Subsequent calls are faster, no reloading needed
  my_package.proc2();
END;

