PROCEDURE get_report(p_type IN VARCHAR2, p_data OUT SYS_REFCURSOR) IS
  l_query VARCHAR2(500);
BEGIN
  IF p_type = 'SALES' THEN
    l_query := 'SELECT * FROM sales_view';
  ELSE
    l_query := 'SELECT * FROM inventory_view';
  END IF;
  OPEN p_data FOR l_query;
END;

DECLARE
  CURSOR c_emp IS SELECT last_name FROM employees;
BEGIN
  -- No explicit OPEN, FETCH, or CLOSE is needed here.
  FOR emp_rec IN c_emp LOOP
    DBMS_OUTPUT.PUT_LINE(emp_rec.last_name);
  END LOOP;
END;

