CREATE TYPE Person_obj AS OBJECT (
  idno   NUMBER,
  name   VARCHAR2(30),
  MEMBER FUNCTION display RETURN VARCHAR2
);

CREATE TYPE BODY Person_obj IS
  MEMBER FUNCTION display RETURN VARCHAR2 IS
  BEGIN
    RETURN 'ID: ' || self.idno;
  END;
END;

CREATE TYPE BODY Person_obj IS
  MEMBER PROCEDURE set_name(p_name VARCHAR2) IS
  BEGIN
    SELF.name := p_name;
  END;
END;
